package com.techzenure.shivakumar.day1;


public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=3;
		
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
		
		System.out.println(10.0f/3);

	}

}
